bool intersect(pair<int, int> lhs, pair<int, int> rhs) {
  if (lhs.first <= rhs.first && rhs.first <= lhs.second) { return true; }
  if (rhs.first <= lhs.first && lhs.first <= rhs.second) { return true; }
  return false;
