      x--; y--;
      cnt[x]++;
      cnt[y]--;
    }
    int prev = -1;
    int ans = n + 1;
    int sum = 0;
    REP(i, n) {
      if (sum == 0 && cnt[i] > 0) {
        prev = i;
      } else if (sum > 0 && sum + cnt[i] == 0) {
        ans += 2 * (i - prev);
      }
      sum += cnt[i];
    }
    assert(sum == 0);
    printf("%d\n", ans);
  }
}

