// BIT /////////////////////////////////////////////////////////////////////////
template<
    typename T = int
>
class bit{
public:
    T *bit_arr;
    int size;

    bit(int n){
        size = n;
        bit_arr = new T[n];
        fill(bit_arr, bit_arr+n, 0);
    }

    ~bit(){
        delete bit_arr;
    }

    T sum(int a){
        T ret = 0;
        while(a){
            ret+=bit_arr[a];
            a-=a&-a;
        }
        return ret;
    }

    T sum(int a, int b){
        return sum(b)-sum(a);
    }

    T get(int a){
        return sum(a, a+1);
    }

    void add(int a, T x){
        a++;
        while(a<size){
            bit_arr[a]+=x;
            a+=a&-a;
        }
    }

    void set(int a, T x){
        add(a, x-get(a));
    }
};
