// RMQ (with index information!) ///////////////////////////////////////////////
template<
    typename T = int,
    class Compare=less<T>
>
class rmq{
public:
    int size;
    Compare comp;
    pair<T, int> *dat;
    T init;

    pair<T, int> mymin(pair<T, int> x, pair<T, int> y){
        if(x.first == y.first) return MP(x.first, min(x.second, y.second));
        else if(min(x.first, y.first, comp)==x.first) return x;
        else return y;
    }

    rmq(int n, T init_=INT_MAX){
        init=init_;
        size = 2;
        while(size<n) size*=2;
        dat = new pair<T, int>[2*size-1];
        REP(i, n) dat[size-1+i]=MP(init, i);
        for(int i=size-2; i>=0; i--) dat[i]=mymin(dat[i*2+1], dat[i*2+2]);
    }

    ~rmq(){
        delete dat;
    }

    void set(int k, T a){
        k+=size-1;
        dat[k]=MP(a, k-(size-1));
        while(k>0){
            k=(k-1)/2;
            dat[k]=mymin(dat[k*2+1], dat[k*2+2]);
        }
    }

    pair<T, int> get(int a, int b, int k, int l, int r){
        if(r<=a || b<=l) return MP(init, -1);
        if(a<=l && r<=b) return dat[k];
        else{
            pair<T, int> vl = get(a, b, k*2+1, l, (l+r)/2);
            pair<T, int> vr = get(a, b, k*2+2, (l+r)/2, r);
            return mymin(vl, vr);
        }
    }

    pair<T, int> getp(int a, int b){
        return get(a, b, 0, 0, size);
    }

    T get(int a, int b){
        return getp(a, b).first;
    }

    int geti(int a, int b){
        return getp(a, b).second;
    }
};

