/*--------------------template--------------------*/
int n, m;
vector<pii> v;
int dp[1111][1111], mx[1111][1111];

void dijkstra(Graph &g, vector<Weight> &d, int s)
{
    d.assign(g.size(), INF);
    d[s] = 0;
    typedef pair<Weight, int> P;
    priority_queue<P, vector<P>, greater<P>> que;
    que.push(P(0, s));
    while (!que.empty())
    {
        Weight dist = que.top().first;
        int v = que.top().second;
        que.pop();
        if (d[v] < dist) continue;
        REP(i, g[v].size())
        {
            Edge e = g[v][i];
            if (d[e.to] > d[v] + e.cost)
            {
                d[e.to] = d[v] + e.cost;
                que.push(P(d[e.to], e.to));
            }
        }
    }
}


int main()
{
    cin >> n >> m;
    REP(i, m)
    {
        int c, d; cin >> c >> d;
        v.emplace_back(c, d);
    }
    sort(ALL(v));
    REP(i, m) mx[i][i] = v[i].second;
    REP(i, m)FOR(j, i + 1, m) mx[i][j] = max(mx[i][j - 1], v[j].second);
    Graph g(m+1);
    REP(i, m)FOR(j, i, m)
    {
        add_edge(g, i, j + 1, mx[i][j] - v[i].first);
    }
    vector<Weight> dist;
    dijkstra(g, dist, 0);
    cout << n + 1 + dist[m] * 2 << endl;
    return 0;
}

