bool opt_debug = false;

int main(int argc, char** argv) {
	std::ios_base::sync_with_stdio(false);
	// set options {{{
	int __c;
	while ( (__c = getopt(argc, argv, "d")) != -1 ) {
		switch (__c) {
			case 'd':
				opt_debug = true;
				break;
			default:
				abort();
		}
	}
	// }}}

	// opt_debug = true;
	// input("./inputs/0.txt");
	// output("./outputs/0.txt");

	int N, m; cin >> N >> m;
	VI dependencies(N+1, 0);
	REP (i, m) {
		int c, d; cin >> c >> d;
		dependencies[c] = d;
	}
	dump(dependencies);

	int ans = 0;
	int back = INF;
	map<int, int> backto;
	FOR (i, 1, N+1) {
		ans++;
		if (EXIST(backto, i)) {
			backto.erase(i);
			if (backto.size() == 0) {
				ans += (i - back) * 2;
				back = INF;
			}
		}
		if (dependencies[i] != 0) {
			if (dependencies[i] > i) {
				if (EXIST(backto, dependencies[i])) {
					backto[dependencies[i]] = min(backto[dependencies[i]], i);
				} else {
					backto[dependencies[i]] = i;
				}
				back = min(back, i);
			}
		}
	}
	cout << ans + 1 << endl;

