int main() {
	cin >> n >> m;
	if (m == 0) {
		cout << n + 1 << endl;
		return 0;
	}
	rep(i, m) {
		ll c, d;
		cin >> c >> d;
		vp2.push_back({ c,d });
	}
	sort(ALL(vp2));
	rep(i, 510)rep(j, 1010)dp[i][j] = INF;
	dp[0][0] = 0;
	int now = 0;
	rep(i, vp2.size()) {
		ll ci = vp2[i].first;
		ll di = vp2[i].second;
		FOR(j, di, n + 2) {//新しく経由する点
