#define PRIME_MAX 1000000
bool isprime[PRIME_MAX+5];
void init_prime(){
    fill(isprime, isprime+PRIME_MAX+5, true);
    isprime[0]=isprime[1]=false;
    REP(i, PRIME_MAX+5){
        if(isprime[i]){
            for(int j=i*2; j<PRIME_MAX; j+=i){
                isprime[j]=false;
            }
        }
    }
}

vll factors(ll n){
    vll v;
    REPL(i, PRIME_MAX){
        if(!isprime[i]) continue;
        while(n%i==0){
            v.PB(i);
            n/=i;
        }
    }
    if(n>1){
        v.PB(n);
    }
    return v;
}

// BIT /////////////////////////////////////////////////////////////////////////
template<
    typename T = int
>
class bit{
public:
    T *bit_arr;
    int size;

    bit(int n){
        size = n;
        bit_arr = new T[n];
        fill(bit_arr, bit_arr+n, 0);
    }

    ~bit(){
        delete bit_arr;
    }

    T sum(int a){
        T ret = 0;
        while(a){
            ret+=bit_arr[a];
            a-=a&-a;
        }
        return ret;
    }

    T sum(int a, int b){
        return sum(b)-sum(a);
    }

    T get(int a){
        return sum(a, a+1);
    }

    void add(int a, T x){
        a++;
        while(a<size){
            bit_arr[a]+=x;
            a+=a&-a;
        }
    }

    void set(int a, T x){
        add(a, x-get(a));
    }
};
