#define REP(i, n) for (int i = 0; i < (int)(n); i++)
#define FOR(i, s, n) for (int i = (s); i < (int)(n); i++)
#define FOREQ(i, s, n) for (int i = (s); i <= (int)(n); i++)
#define FORIT(it, c) for (__typeof((c).begin())it = (c).begin(); it != (c).end(); it++)
#define MEMSET(v, h) memset((v), h, sizeof(v))

int n, m;
vector<pair<int, int> > constraint;
bool used[1000];

bool intersect(pair<int, int> lhs, pair<int, int> rhs) {
  if (lhs.first <= rhs.first && rhs.first <= lhs.second) { return true; }
  if (rhs.first <= lhs.first && lhs.first <= rhs.second) { return true; }
  return false;
}

int main() {
  while (scanf("%d %d", &n, &m) > 0) {
    constraint.clear();
    REP(i, m) {
      int x, y;
      scanf("%d %d", &x, &y);
      if (x > y) { continue; }
      constraint.push_back(make_pair(y, x));
    }
    sort(constraint.begin(), constraint.end());
    REP(i, constraint.size()) { swap(constraint[i].first, constraint[i].second); }
    MEMSET(used, false);
    int ans = n + 1;
    REP(i, constraint.size()) {
      if (used[i]) { continue; }
      pair<int, int> range = constraint[i];
      used[i] = true;
      FOR(j, i, constraint.size()) {
        if (used[j]) { continue; }
        if (intersect(range, constraint[j])) {
          used[j] = true;
          range = make_pair(min(range.first, constraint[j].first), max(range.second, constraint[j].second));
        }
      }
      ans += 2 * abs(range.second - range.first);
    }
    printf("%d\n", ans);
  }
}

