int step[MAX];

void solve(void){
    int N, m;
    cin >> N >> m;
    //累積和を用いることでO(N)に計算量が落とせる
    //半開区間[a, b)で持つ
    rep(m, i) {
        int a, b;
        scanf("%d %d\n", &a, &b);
        a--; b--;
        step[a]++;
