    }
    rep(N, i) step[i+1] += step[i];
    int dis = 0, ans = 0;
    rep(N+1, n) {
        if (step[n] > 0) dis++;
        else {
            ans += dis;
            dis = 0;
        }
    }
    ans = ans*2+1+N;
    cout << ans << '\n';
}

