int N, m;

int main() {
	cin >> N >> m;

	typedef pair< int, int > pii;
	set< pii > interval;

	for (int i=0; i<m; ++i) {
		int c, d;
		cin >> c >> d;

		// left side
		auto it = interval.lower_bound(pii(c, 0));
		if (it != interval.begin()) {
			--it;
			if (c <= (*it).second) {
				c = (*it).first;
				d = max(d, (*it).second);
				interval.erase(it);
			}
		}

		// right side
		it = interval.lower_bound(pii(c, 0));
		if (it != interval.end()) {
			if (c <= (*it).first && (*it).first <= d) {
				d = max(d, (*it).second);
				interval.erase(it);
			}
		}

		interval.insert(pii(c, d));
	}

	int ans = N+1;
	for (pii p : interval) {
		int len = p.second - p.first;
		ans += 2 * len;
	}
	cout << ans << endl;
}

