extern crate core;

use std::fmt;
use std::cmp::{Ordering, min, max};
use std::fmt::{Display, Error, Formatter};
use std::ops::{Index, IndexMut};
use std::collections::VecDeque;
use std::f32::MAX;

fn show<T: Display>(vec: &Vec<T>) {
    if vec.is_empty() {
        println!("[]");
    }else {
        print!("[{}", vec[0]);
        for i in 1 .. vec.len() {
            print!(", {}", vec[i]);
        }
        println!("]");
    }
}
macro_rules! read_line{
    () => {{
        let mut line = String::new();
        std::io::stdin().read_line(&mut line).ok();
        line
    }};
    (delimiter: ' ') => {
        read_line!().split_whitespace().map(|x|x.to_string()).collect::<Vec<_>>()
    };
    (delimiter: $p:expr) => {
        read_line!().split($p).map(|x|x.to_string()).collect::<Vec<_>>()
    };
    (' ') => {
        read_line!(delimiter: ' ')
    };
    ($delimiter:expr) => {
        read_line!(delimiter: $delimiter)
    };
    (' '; $ty:ty) => {
        read_line!().split_whitespace().map(|x|x.parse::<$ty>().ok().unwrap()).collect::<Vec<$ty>>()
    };
    ($delimiter:expr; $ty:ty) => {
        read_line!($delimiter).into_iter().map(|x|x.parse::<$ty>().ok().unwrap()).collect::<Vec<$ty>>()
    };
}
macro_rules! let_all {
    ($($n:ident:$t:ty),*) => {
        let line = read_line!(delimiter: ' ');
        let mut iter = line.iter();
        $(let $n:$t = iter.next().unwrap().parse().ok().unwrap();)*
    };
}
fn main(){
    let_all!(n: usize, m: usize);
    let mut sections = Vec::with_capacity(m);
    for _ in 0 .. m {
        let_all!(c: usize, d: usize);
        sections.push(Section{from: c, to: d});
    }
    sections.sort_by_key(|s|s.from);
    let mut current = Section{from: 0, to: 0};
    let mut result = 0;
    for s in &sections {
        if current.has_intersection(s) {
            current = current.merged(s);
        }else {
            result += current.to - current.from;
            current = *s;
        }
    }
    result += current.to - current.from;
    println!("{}", result * 2 + n + 1);
}
#[derive(Copy, Clone)]
struct Section {
    from: usize, to: usize,
}
impl Display for Section {
    fn fmt(&self, f: &mut Formatter) -> Result<(), Error> {
        write!(f, "({}, {})", self.from, self.to)
    }
}
impl Section {
    fn has_intersection(&self, other: &Section) -> bool {
        !self.has_no_intersection(other)
    }
    fn has_no_intersection(&self, other: &Section) -> bool {
        self.to < other.from || other.to < self.from
    }
    fn merged(&self, other: &Section) -> Section {
        Section{from: min(self.from, other.from), to: max(self.to, other.to)}
    }
}
