using namespace std;

#define REP(i,n) for(int i=0;i<(int)(n);i++)
#define FOR(i,a,b) for(int i=(int)(a);i<(int)(b);i++)
#define FORR(i,a,b) for(int i=(int)(b)-1;i>=(int)(a);i--)

typedef long long ll;

int p,r,t;
int l[353];
int n[353][353];
int nsum[353];
int P[252521], R[252521];

int l2[353], n2[353][353];
int nsum2[353];

int main(){
  scanf("%d%d%d",&p,&r,&t);
  REP(i,r)scanf("%d",l+i);
  REP(i,p)REP(j,r)scanf("%d",&n[i][j]);
  REP(i,t)scanf("%d%d",P+i,R+i);
  REP(i,p){
    nsum[i] = 0;
    REP(j,r)nsum[i] += n[i][j];
  }
  REP(i,t)P[i]--, R[i]--;
  int low = -1, high = t+1;
  while(low+1 < high){
    int x = (low+high)/2;
    REP(i,r)l2[i] = l[i];
    REP(i,p)REP(j,r)n2[i][j] = n[i][j];
    REP(i,p)nsum2[i] = nsum[i];
    REP(i,x){
      l2[R[i]]--;
      n2[P[i]][R[i]]--;
      nsum2[P[i]]--;
      if(nsum2[P[i]] == 0){
        REP(j,r){
          l2[j] += n[i][j];
          n2[P[i]][j] = n[i][j];
        }
        nsum2[P[i]] = nsum[P[i]];
      }
    }
    vector<bool> avoided(p, false);
    int cnt = 0;
    REP(_,p)REP(i,p)if(!avoided[i]){
      bool ok = true;
      REP(j,r)if(n2[i][j] > l2[j])ok=false;
      if(ok){
        REP(j,r)l2[j] += (n[i][j]-n2[i][j]);
        avoided[i] = true;
        cnt++;
      }
    }
    if(cnt == p){
      low = x;
    }else{
      high = x;
    }
  }
  printf("%d\n", high);
  return 0;
}

