int q[maxN][maxN], p[maxN];
bool deadlock(int w) {
	memcpy(q, nd, sizeof nd);
	memcpy(p, l, sizeof l);
	memset(li, 0, sizeof li);
	for (int i = 1; i <= w; ++i) {
		--q[ope[i].x][ope[i].y];
		--p[ope[i].y];
	}
	for (int i = 1; i <= n; ++i)
		for (int j = 1; j <= n; ++j) {
			bool ok = true;
			for (int k = 1; k <= m; ++k)
				if (p[k] < q[j][k]) {
					ok = false;
					break;
				}
			if (ok) {
				for (int k = 1; k <= m; ++k)
					p[k] += nd[j][k] - q[j][k];
				li[j] = true;
			}
		}
	for (int i = 1; i <= n; ++i)
		if (!li[i]) return true;
	return false;

}

