int n,m,p;
int now[N],sum[N];
int need[N][N],has[N];
int sneed[N][N],remain[N];
int ax[M],ay[M];
bool inq[N];

bool check(int t)
{
	int i,j,k;
	int a,b;


	for(i=1;i<=m;i++)remain[i]=has[i];
	for(i=1;i<=n;i++)for(j=1;j<=m;j++)
		sneed[i][j]=need[i][j];
	for(i=1;i<=n;i++)inq[i]=1,now[i]=0;

	for(i=1;i<=t;i++)
	{
		a=ax[i],b=ay[i];
		now[a]++,sneed[a][b]--,remain[b]--;
		if(now[a]==sum[a])
		{
			for(i=1;i<=m;i++)
				remain[i]+=need[a][i];
			inq[a]=0;
		}
	}
	bool flag=1;
	while(flag)
	{
		flag=0;
		for(i=1;i<=n;i++)if(inq[i])
		{
			for(j=1;j<=m;j++)
				if(sneed[i][j]>remain[j])
					break;
			if(j>m)
			{
				for(j=1;j<=m;j++)
					remain[j]+=need[i][j]-sneed[i][j];
				inq[i]=0;
				flag=1;
			}
		}
	}
	for(i=1;i<=n;i++)if(inq[i])return 0;
	return 1;
}
