bool deadlock(int w) {
	memcpy(rec, nd, sizeof nd);
	memcpy(rec2, l, sizeof l);
	memset(li, false, sizeof li);
	for (int i = 1; i <= w; ++i) {
		nd[ope[i].x][ope[i].y]--;
		l[ope[i].y]--;
	}
