			bool ok = true;
			for (int k = 1; k <= m; ++k)
				if (l[k] < nd[i][k]) {
					ok = false;
					break;
				}
			if (ok) {
				for (int k = 1; k <= m; ++k)
					l[k] += rec[i][k] - nd[i][k];
				li[i] = true;
			}
		}
	memcpy(nd, rec, sizeof rec);
	memcpy(l, rec2, sizeof rec2);
	for (int i = 1; i <= n; ++i)
		if (!li[i]) return true;
	return false;
}

