        REP(i, P) REP(j, R) cin >> need_[i][j];

        vector<int> X(K);
        vector<int> Y(K);
        REP(i, K) cin >> X[i] >> Y[i];
        REP(i, K) X[i]--;
        REP(i, K) Y[i]--;

