            REP(i, P) {
                REP(j, R) if(need_[i][j] > all[j]) return true;
            }
            return false;
        };
        assert(!zero());

        auto check = [&](int T) {
            vector<int> cur = all;
            vector<vector<int>> need = need_;
            vector<vector<int>> take(P, vector<int>(R, 0));
            vector<int> have(P);
            vector<int> rest(P);
            REP(i, P) REP(j, R) rest[i] += need[i][j];

