                cur[ Y[i] ]--;
                need[ X[i] ][ Y[i] ]--;
                take[ X[i] ][ Y[i] ]++;
                have[ X[i] ]++;
                if(--rest[X[i]] == 0) {
                    have[X[i]] = 0;
                    REP(j, R) {
                        cur[j] += take[ X[i] ][j];
                    }
                }
            }

