    REP(i,r)l2[i] = l[i];
    REP(i,p)REP(j,r)n2[i][j] = n[i][j];
    REP(i,p)nsum2[i] = nsum[i];
    REP(i,x){
      l2[R[i]]--;
      n2[P[i]][R[i]]--;
      nsum2[P[i]]--;
      if(nsum2[P[i]] == 0){
        REP(j,r){
          l2[j] += n[P[i]][j];
          n2[P[i]][j] = n[P[i]][j];
        }
        nsum2[P[i]] = nsum[P[i]];
      }
    }
    vector<bool> avoided(p, false);
    int cnt = 0;
    REP(_,p)REP(i,p)if(!avoided[i]){
      bool ok = true;
      REP(j,r)if(n2[i][j] > l2[j])ok=false;
      if(ok){
        REP(j,r)l2[j] += (n[i][j]-n2[i][j]);
        avoided[i] = true;
        cnt++;
      }
    }
    if(cnt == p){
      low = x;
    }else{
      high = x;
    }
  }
  printf("%d\n", high);
  return 0;
}

