#define  fr(i,x,y) for(i=x;i<=y;i++)
#define  frd(i,x,y) for(i=x;i>=y;i--)
const int maxn=310;
const int maxm=(int)2e5+10;
int n,m,T,p;
int have[maxn],a[maxn];
int want[maxn][maxn],ori[maxn][maxn];
int list[maxm][2];
bool used[maxn];
void Read()
{	
	int i,j;
	scanf("%d%d%d",&n,&m,&T);
	fr(j,1,m)scanf("%d",&have[j]);
	fr(i,1,n)
	fr(j,1,m)
		scanf("%d",&want[i][j]),ori[i][j]=want[i][j];
	fr(i,1,T)scanf("%d%d",&list[i][0],&list[i][1]);
}
void Go_up(int low,int high)
{
	int i,p,r;
	fr(i,low+1,high)
	{
		p=list[i][0],r=list[i][1];
		have[r]--;
		want[p][r]--;
	}
}
void Go_down(int high,int low)
{
	int i,p,r;
	frd(i,high,low+1)
	{
		p=list[i][0],r=list[i][1];
		have[r]++;
		want[p][r]++;
	}
}
bool Check()
{
	int i,j,cnt;
	bool bo;
	memcpy(a+1,have+1,m*sizeof(int));
	cnt=0;
	bo=true;
	memset(used+1,false,n*sizeof(bool));
	while(bo)
	{
		bo=false;
		fr(i,1,n)if(!used[i])
		{
			fr(j,1,m)if(want[i][j]>a[j])break;
			if(j<=m)continue;
			cnt++;
			fr(j,1,m)a[j]+=ori[i][j]-want[i][j];
			bo=true;
			used[i]=true;
			break;
		}
	}
	return cnt==n;
}
void Solve()
{
	int le,ri,mi;
	le=1,ri=T;
	p=0;
	while(le<=ri)
	{
		mi=(le+ri)>>1;
		if(mi>p)Go_up(p,mi);
		else Go_down(p,mi);
		p=mi;
		if(Check())le=mi+1;
		else ri=mi-1;
	}
	if(le>T)le=-1;
	printf("%d\n",le);
}
int main()
{
	Read();
	Solve();
}

