	cin >> p >> r >> t;
	vi resource(r);
	REP(i, r) cin >> resource[i];
	vector<vi> need(p, vi(r));
	REP(i, p)REP(j, r) cin >> need[i][j];
	vector<pii> query;
	REP(i, t)
	{
		int a, b; cin >> a >> b;
		a--; b--;
		query.emplace_back(a, b);
	}		
	auto available = resource;
	auto tmp = need;
	REP(i, t)
	{
		int p = query[i].first, r = query[i].second;
		available[r]--;
		tmp[p][r]--;
	}
	int lb = 0, ub = t + 1;
	while (ub - lb > 1)
	{
		int time = (ub + lb) / 2;
		auto available = resource;
		auto tmp = need;
		REP(i, time)
		{
			int p = query[i].first, r = query[i].second;
			available[r]--;
			tmp[p][r]--;
		}
		vector<bool> f(p, true);
		REP(i, p)REP(j, tmp[i].size())
		{
			if (tmp[i][j] > available[j]) f[i] = false;
		}
		REP(i, p)
		{
			if (f[i])
			{
				REP(j, r) available[j] += need[i][j] - tmp[i][j];
			}
		}
		bool ok = true;
		REP(i, p)
		{
			if (!f[i])
			{
				REP(j, r)
				{
					if (tmp[i][j] > available[j]) ok = false;
				}
			}
		}
		if (ok) lb = time;
		else ub = time;
	}
	if (ub == t + 1) cout << -1 << endl;
	else cout << ub << endl;
	return 0;
}

