    memset(bo, 0, sizeof(bo));
    for (int i = 1; i <= t; ++i) {
        ++used[P[i].second];
        ++grid[P[i].first][P[i].second];
    }
    int tot = 0;
    for (int i = 1; i <= p; ++i) {
        int mark = 0;
        for (int j = 1; j <= r; ++j)
            if (grid[i][j] < ned[i][j]) {
                mark = 1;
                break;
            }
        if (mark == 0) {
            bo[i] = 1;
            ++tot;
            for (int j = 1; j <= r; ++j)
                used[j] -= grid[i][j];
        }
    }
    while (1) {
        int flag = 0;
        for (int i = 1; i <= p; ++i)
            if (!bo[i]) {
                int mark = 0;
                for (int j = 1; j <= r; ++j)
                    if (grid[i][j] + num[j] - used[j] < ned[i][j]) {
                        mark = 1;
                        break;
                    }
                if (mark == 0) {
                    flag = i;
                    break;
                }
            }
        if (flag == 0) break;
        bo[flag] = 1;
        ++tot;
        for (int j = 1; j <= r; ++j)
            used[j] -= grid[flag][j];
    }
    return tot == p;
}

