bool check(int mid) {
    queue <int> q;
    for (int i = 0; i < p; i++) {
        vis[i] = false;
        for (int j = 0; j < r; j++) {
            s[i][j] = m[i][j];
        }
    }
    for (int i = 0; i < r; i++) {
        b[i] = a[i];
    }
    for (int i = 0; i <= mid; i++) {
        b[v[i]]--;
        s[u[i]][v[i]]--;
    }
    for (int i = 0; i < p; i++) {
        bool ok = true;
        for (int j = 0; j < r; j++) {
            if (s[i][j] > b[j]) {
                ok = false;
                break;
            }
        }
        if (ok) {
            vis[i] = true;
            q.push(i);
        }
    }
    while (!q.empty()) {
        int now = q.front();
        q.pop();
        for (int i = 0; i < r; i++) {
            b[i] += m[now][i] - s[now][i];
        }
        for (int i = 0; i < p; i++) {
            if (vis[i]) {
                continue;
            }
            bool ok = true;
            for (int j = 0; j < r; j++) {
                if (s[i][j] > b[j]) {
                    ok = false;
                    break;
                }
            }
            if (ok) {
                vis[i] = true;
                q.push(i);
            }
        }
    }
    for (int i = 0; i < p; i++) {
        if (!vis[i]) {
