class state{
public:
	int p[305][305];
	int r[305];
	inline bool off(int i){
		for (int j=1;j<=R;++j) if (p[i][j]) return 0;
		return 1;
	}
	inline bool on(int i){
		for (int j=1;j<=R;++j) if (p[i][j]) return 1;
		return 0;
	}
	inline bool available(int i){
		for (int j=1;j<=R;++j) if (p[i][j]>r[j]) return 0;
		return 1;
	}
	inline void release(int i){
		for (int j=1;j<=R;++j) r[j]+=pp[i][j];
	}
	inline bool judge(){// O(n^3)
		int p1[305][305],r1[305];
		rep(i,305) rep(j,305) p1[i][j]=p[i][j];
		rep(j,305) r1[j]=r[j];
		while(1){
			int cnt=0;
			for (int i=1;i<=P;++i) if (on(i)) ++cnt;
			if (!cnt){
				rep(i,305) rep(j,305) p[i][j]=p1[i][j];
				rep(j,305) r[j]=r1[j];
				return 1;
			}
			bool term=1;
			for (int i=1;i<=P;++i){
				if (off(i)) continue;
				if (available(i)){
					term=0;
					for (int j=1;j<=R;++j){
						r[j]-=p[i][j];p[i][j]=0;r[j]+=pp[i][j];
					}
					break;
				}
			}
			if (term){
				rep(i,305) rep(j,305) p[i][j]=p1[i][j];
				rep(j,305) r[j]=r1[j];
				return 0;
			}
		}

	}
	state operator = (const state& st){
		rep(i,305) rep(j,305) p[i][j]=st.p[i][j];
		rep(j,305) r[j]=st.r[j];
		return *this;
	}
};

