	for (int i = 0; i < P; ++i) {
		int cnt = 0;
		for (int j = 0; j < R; ++j) {
			cnt += p[i][j] == 0;
		}
		if (cnt == R) {
			isFree[i] = true;
			for (int j = 0; j < R; ++j) {
				r[j] += p0[i][j];
			}
		}
	}

	vector<int> deadCount(P, 0);
	vector<bool> isDead(P, false);
	vector< set<Pair> > need(R);
	for (int i = 0; i < P; ++i) {
		for (int j = 0; j < R; ++j) {
			if (p[i][j] > r[j]) {
				need[j].insert( Pair(p[i][j], i) );
				++deadCount[i];
				isDead[i] = true;
			}
		}
	}

	queue<Pair> Q;
	for (int i = 0; i < P; ++i) {
		if (!isFree[i] && !isDead[i]) {
			isFree[i] = true;
			for (int j = 0; j < R; ++j) {
				Q.push( Pair(j, p0[i][j]) );
			}
		}
	}
	while ( !Q.empty() ) {
		Pair q = Q.front(); Q.pop();
		int rid = q.first, amount = q.second;
		r[rid] += amount;
		while (need[rid].size() > 0) {
			set<Pair>::iterator it = need[rid].begin();
			if (it->first <= r[rid]) {
				if (--deadCount[it->second] == 0) {
					isDead[it->second] = false;
					isFree[it->second] = true;
					for (int j = 0; j < R; ++j) {
						Q.push( Pair(j, p0[it->second][j]) );
					}
				}
				need[rid].erase(it);
			}
			else {
				break;
			}
		}
	}
	for (int i = 0; i < P; ++i) {
		if (isDead[i]) return false;
	}

	return true;
}

