int need[300][300];
int R[200000];
int P[200000];
typedef pair<int, int> Pii;
bool check(int x){
	int sum[300][300] = { 0 };
	int rem[300] = { 0 };
	memcpy(rem, res, sizeof res);

	for(int i = 0; i < x; i++){
		if(rem[R[i]] == 0) return false;
		rem[R[i]]--;
		sum[P[i]][R[i]]++;
	}
	int fin[300] = { 0 };

	set<Pii> needrem[300];// rem, idx
	queue<int> q;
	for(int i = 0; i < N; i++){
		for(int j = 0; j < r; j++){
			int c = need[i][j] - sum[i][j];
			if(c <= rem[j]) fin[i]++;
		}
		if(fin[i] == r) q.push(i);
		else{
			for(int j = 0; j < r; j++){
				int c = need[i][j] - sum[i][j];
				if(c > rem[j]) needrem[j].insert({ c, i });
			}
		}
	}
	while(q.size()){
		int id = q.front();
		q.pop();
		for(int j = 0; j < r; j++){
			if(sum[id][j] == 0) continue;
			rem[j] += sum[id][j];
			while(needrem[j].size() && needrem[j].begin()->first <= rem[j]){
				auto it = needrem[j].begin();
				int id2 = it->second;
				fin[id2]++;
				if(fin[id2] == r) q.push(id2);
				needrem[j].erase(it);
			}
		}
	}
	for(int i = 0; i < N; i++){
		if(fin[i] != r) return false;
	}
	return true;
}


