struct erz{int p,r;}e[200005];
int a[N][N],nd[N][N],lp[N],dn[N],bg[N],ed[N],nd0[N][N],lp0[N],q[N],J;
bool cmp(int x,int y) {return(nd[x][J]<nd[y][J]);}
int check(int n,int m,int t)
{
    int i,j,h,sl=n;
    for (i=1;i<=n;i++) dn[i]=0;
    for (i=1;i<=m;i++) lp[i]=lp0[i];
    for (i=1;i<=n;i++)
     for (j=1;j<=m;j++)
     {
         if (nd0[i][j]) dn[i]++;
         nd[i][j]=nd0[i][j];
     }

    for (i=1;i<=t;i++)
    {
        int pp=e[i].p,rr=e[i].r;
        nd[pp][rr]--;lp[rr]--;
        if (!nd[pp][rr]) dn[pp]--;
        if (!dn[pp]) {sl--;for (j=1;j<=m;j++) lp[j]+=nd0[pp][j];}
    }
    if (!sl) return 1;
    h=1,t=0;
    for (j=1;j<=m;j++) ed[j]=0;
    for (i=1;i<=n;i++)
    {
        if (!dn[i]) continue;
        for (j=1;j<=m;j++) if (nd[i][j]>lp[j]) break;
        if (j==m+1) {dn[i]=0;q[++t]=i;continue;}
        for (j=1;j<=m;j++) if (nd[i][j]) a[j][++ed[j]]=i;
    }
    for (j=1;j<=m;j++) {J=j;bg[j]=1;sort(a[j]+1,a[j]+ed[j]+1,cmp);}
    while (h<=t)
    {
        int x=q[h];sl--;if (!sl) return 1;
        for (j=1;j<=m;j++)
        {
            lp[j]+=nd0[x][j]-nd[x][j];
            for (i=bg[j];i<=ed[j];i++)
            {
                 if (nd[a[j][i]][j]>lp[j]) {bg[j]=i;break;}
                 dn[a[j][i]]--;if (dn[a[j][i]]) continue;
                 q[++t]=a[j][i];
            }
        }
        h++;
    }
    return 0;
}
