  rep(i,R)scanf("%d",init+i);
  rep(i,P)rep(j,R)scanf("%d",&need[i][j]);
  rep(i,T){
    scanf("%d%d",p+i,r+i);
    p[i]--;r[i]--;
  }

  int lb=0,ub=T+1;
  while(ub-lb>1){
    int mid=(lb+ub)/2;
    if(f(mid))lb=mid;
    else ub=mid;
  }
  if(ub==T+1)cout<<-1<<endl;
  else cout<<ub<<endl;
  return 0;
}

