                for(int i = 1; i <= p; i++) {
                    for(int j = 1; j <= r; j++){
                        if(n[i][j] > l[j]) bu[i]++;
                    }
                    if(!bu[i]) cnt++;
                }

            }
        }

        for(int j = 1; j <= p; j++) {
            if(n[j][R[i]] == l[R[i]] + 1) {
                bu[j]++;
                if(bu[j] == 1) cnt--;
            }
        }
        if(!cnt) {
            printf("%d\n",i);
            return 0;
        }

    }
    printf("-1\n");
}

