const int INF = 1 << 30;
const ll INFL = 1LL << 60;


class Solver {
  public:
    int P, R, T;
    vector<int> L;
    vector<vector<int>> N;
    vector<pii> PR;

    // O(t)
    tuple<vector<vector<int>>, vector<int>> calc_state(int t) {
        vector<vector<int>> need = N;
        vector<int> left;
        left = L;
        rep(i, t) {
            int p = PR[i].first, r = PR[i].second;
            left[r]--;
            need[p][r]--;
        }
        cerr << "state t = " << t << endl;
        cerr << "left " << left << endl;
        cerr << need << endl;
        return make_tuple(need, left);
    }

    bool is_avoidable(int t) {
        vector<vector<int>> need;
        vector<int> left;
        tie(need, left) = calc_state(t);

        // check lack of resource
        for(int l : left) if(l < 0) return false;

        vector<bool> terminated(P);
        int terminated_num = 0;
        bool flg = true;
        while(terminated_num < P and flg) {
            flg = false;
            rep(p, P) {
                if(terminated[p]) continue;
                bool terminatable = true;
                rep(r, R) {
                    if(need[p][r] > left[r]) {
                        terminatable = false;
                        break;
                    }
                }
                if(terminatable) {
                    rep(r, R) {
                        left[r] += N[p][r];
                    }
                    terminated[p] = true;
                    terminated_num++;
                    flg = true;
                }                
            }
        }
        cerr << "! " << t << " " << terminated_num << endl;
        return terminated_num == P;
    }

    bool solve() {
        cin >> P >> R >> T;
        L.resize(R); cin >> L;
        N.resize(P, vector<int>(R)); cin >> N;
        PR.resize(T);
        rep(i, T) {
            cin >> PR[i].first >> PR[i].second;
            PR[i].first--;
            PR[i].second--;
        }

        int l = 0, r = T + 1;
        while(l + 1 < r) {
            int m = (l + r) / 2;
            (is_avoidable(m) ? l : r) = m;            
        }
        cout << (r > T ? -1 : r) << endl;
        return 0;
    }
};

