using namespace std;

int change(char ch){
  if('A'<=ch&&ch<='Z'){
    return ch-'A';
  }else{
    return 26+ch-'a';
  }
}

int N;
long double po[60];
char mp[50][50];
int t[50][50];

struct edge{
  int to;
  int cap;
  long double cost;
  int rev;
};

#define MAX_V (105)

vector<edge> G[MAX_V];
long double h[MAX_V];
long double dist[MAX_V];
int prevv[MAX_V],preve[MAX_V];
long double eps=0.00000001;

void add_edge(int from,int to,int cap,long double cost){
  G[from].push_back( (edge){to,cap,cost,(int)G[to].size() } );
  G[to].push_back(   (edge){from,0,-cost,(int)G[from].size()-1 } );
}

typedef pair< long double , int > P;

long double min_cost_flow(int s,int t,int f){
  long double res=0;
  while(f>0){
    priority_queue< P , vector<P> , greater<P> > Q;
    fill(dist,dist+MAX_V, po[59]);
    dist[s]=0;
    Q.push(P(0,s));
    while(!Q.empty()){
      P p=Q.top();Q.pop();
      int v=p.second;
      if(dist[v]<p.first)continue;

      for(int i=0;i<(int)G[v].size();i++){
        edge &e=G[v][i];
        if(e.cap>0&&dist[e.to]-eps>dist[v]+e.cost+h[v]-h[e.to]){
          dist[e.to]=dist[v]+e.cost+h[v]-h[e.to];
          prevv[e.to]=v;
          preve[e.to]=i;
          Q.push(P(dist[e.to],e.to));
        }
      }
    }

    if(dist[t]==po[59])return -1;
    for(int v=0;v<MAX_V;v++)h[v]+=dist[v];
    int d=f;
    for(int v=t;v!=s;v=prevv[v])
      d=min(d,(int)G[prevv[v]][preve[v]].cap);
    f-=d;
    res+=(long double)d*h[t];
    for(int v=t;v!=s;v=prevv[v]){
      edge &e=G[prevv[v]][preve[v]];
      e.cap-=d;
      G[v][e.rev].cap+=d;
    }
  }
  return res;
}

int main(){
  po[0]=1;

  cin>>N;
  int si=N+N;
  int ti=N+N+1;

  map< char , int > cnt;
  for(int i=0;i<N;i++){
    for(int j=0;j<N;j++){
      char ch;
      cin>>ch;
      cnt[ change(ch) ]++;
      mp[i][j]=ch;
    }
  }

  po[0]=1;
  for(int i=1;i<60;i++){
    po[i]=po[i-1]*( long double)( cnt[i-1]+1 );
  }

  for(int i=0;i<N;i++){
    for(int j=0;j<N;j++){
      t[i][j]=G[i].size();
      add_edge(i,N+j,1,po[ change(mp[i][j]) ]);
    }
  }

  for(int i=0;i<N;i++){
    add_edge(si,i,1,0);
    add_edge(N+i,ti,1,0);
  }

  long double ansf=min_cost_flow(si,ti,N);

  string ans="";
  for(int i=0;i<N;i++){
    for(int j=0;j<N;j++){
      edge e=G[i][ t[i][j] ];
      if(e.cap==0)ans+=mp[i][j];
    }
  }
  sort(ans.begin(),ans.end());
  cout<<ans<<endl;
  return 0;
}

