		if(cnt[i]==r){
			used[i]=true;
		}
	}
	int restcnt=n;
	for(int i=0;i<vt;i++){
		if(tmpl[req[i]]==0)continue;
		tmpf[p[i]][req[i]]++;
		tmpl[req[i]]--;
		if(tmpf[p[i]][req[i]]==0)cnt[p[i]]++;
		if(cnt[p[i]]==r){
			used[p[i]]=true;
			restcnt--;
			for(int j=0;j<r;j++){
				tmpl[j]+=tmpf[p[i]][j];
			}
		}
	}
	bool update=true;
	while(update){
		update=false;
		for(int i=0;i<n;i++){
			if(!used[i]){
				bool flag=true;
				for(int j=0;j<r;j++){
					if(fie[i][j]-tmpf[i][j]>tmpl[j])flag=false;
