const int INF = 1 << 30;
const ll INFL = 1LL << 60;


class Solver {
  public:
    int P, R, T;
    vector<int> L;
    vector<vector<int>> N;
    vector<pii> PR;

    bool check_terminated(vector<int> &need) {
        bool terminated = true;
        rep(r, R) terminated &= need[r] == 0;
        return terminated;
    }

    // O(t)
    tuple<bool, vector<vector<int>>, vector<int>> calc_state(int t) {
        vector<vector<int>> need = N;
        vector<int> left = L;
        rep(i, t) {
            int p = PR[i].first, r = PR[i].second;
            left[r]--;
            need[p][r]--;
            if(left[r] < 0) {
                return make_tuple(false, need, left);
            }
            if(need[p][r] == 0) {
                if(check_terminated(need[p])) {
                    rep(r, R) left[r] += N[p][r];
                }
            }
        }
        // cerr << "state t = " << t << endl;
        // cerr << "left " << left << endl;
        // cerr << need << endl;
        return make_tuple(true, need, left);
    }

    bool is_avoidable(int t) {
        bool reachable;
        vector<vector<int>> need;
        vector<int> left;
        tie(reachable, need, left) = calc_state(t);

        // check lack of resource
        if(not reachable) return false;

        vector<bool> terminated(P);
        int terminated_num = 0;
        bool flg = true;
        while(terminated_num < P and flg) {
            flg = false;
            rep(p, P) {
                if(terminated[p]) continue;
                bool terminatable = true;
                rep(r, R) {
                    if(need[p][r] > left[r]) {
                        terminatable = false;
                        break;
                    }
                }
                if(terminatable) {
                    rep(r, R) {
                        left[r] += N[p][r];
                    }
                    terminated[p] = true;
                    terminated_num++;
                    flg = true;
                }                
            }
        }
        // cerr << "! " << t << " " << terminated_num << endl;
        return terminated_num == P;
    }

    bool solve() {
        cin >> P >> R >> T;
        L.resize(R); cin >> L;
        N.resize(P, vector<int>(R)); cin >> N;
        PR.resize(T);
        rep(i, T) {
            cin >> PR[i].first >> PR[i].second;
            PR[i].first--;
            PR[i].second--;
        }

        int l = 0, r = T + 1;
        while(l + 1 < r) {
            int m = (l + r) / 2;
            // cerr << m << " " << l << " " << r << endl;
            (is_avoidable(m) ? l : r) = m;            
        }
        cout << (r > T ? -1 : r) << endl;
        return 0;
    }
};

