  for(int i=0;i<p;i++){
    for(int j=0;j<r;j++) cout << n[i][j] << " ";
    cout << endl;
  }
  */


  bool loop = true;
  memset( used,0,sizeof( used ));
  while( loop ){
    loop = false;
    for(int i=0;i<p;i++){
      if( used[i] ) continue;
      if( isRelease( i ) ) {
        for(int j=0;j<r;j++)
          L[ j ] += tmp[i][j] - n[i][j];
        used[i] = true;
        loop = true;
      }
    }
  }

  for(int i=0;i<p;i++)
    if( !used[i] ) return true;
