#define FI(i,a,b) for(int i=(a);i<=(b);i++)
#define FD(i,a,b) for(int i=(a);i>=(b);i--)

using namespace std;

int n, re, T, ini[305], need[305][305];
int acq[200005][2];

int cur[305], cneed[305][305];
bool done[305];

bool ok(int til){
	FI(i, 1, re) cur[i] = ini[i];
	FI(i, 1, n) FI(j, 1, re) cneed[i][j] = need[i][j];
	FI(i, 1, til){
		cur[acq[i][1]]--;
		cneed[acq[i][0]][acq[i][1]]--;
	}
	FI(i, 1, n) done[i] = false;
	//can avoid death?
	int cnt = 0;
	while(cnt < n){
		bool lock = true;
		FI(i, 1, n) if(!done[i]){
			bool feasible = true;
			FI(j, 1, re) if(cneed[i][j] > cur[j]){
				feasible = false;
				break;
			}
			if(feasible){
				done[i] = true;
				lock = false;
				cnt++;
				FI(j, 1, re) cur[j] += need[i][j];
			}
		}
		if(lock) return false;
	}
	return true;
}

int main(){
	scanf("%d %d %d", &n, &re, &T);
	FI(i, 1, re) scanf("%d", &ini[i]);
	FI(i, 1, n) FI(j, 1, re) scanf("%d", &need[i][j]);
	FI(i, 1, T) scanf("%d %d", &acq[i][0], &acq[i][1]);
	int l = -1, r = T + 1;
	while(r - l > 1){
		int m = (l + r) >> 1;
		if(ok(m)) l = m;
		else r = m;
	}
	if(r == T + 1) r = -1;
	printf("%d\n", r);
	return 0;
}

