const int N = 200050;

int p, r, t, a[510], m[510][510], s[510][510], b[510], u[N], v[N];
bool vis[510];

bool check(int mid) {
    queue <int> q;
    for (int i = 0; i < p; i++) {
        vis[i] = false;
        for (int j = 0; j < r; j++) {
            s[i][j] = m[i][j];
        }
    }
    for (int i = 0; i < r; i++) {
        b[i] = a[i];
    }
    for (int i = 0; i <= mid; i++) {
        b[v[i]]--;
        s[u[i]][v[i]]--;
        assert(u[i] < p);
        assert(v[i] < r);
        //assert(b[v[i]] >= 0);
        assert(s[u[i]][v[i]] >= 0);
    }
    for (int i = 0; i < p; i++) {
        bool ok = true;
        for (int j = 0; j < r; j++) {
            if (s[i][j] > b[j]) {
                ok = false;
                break;
            }
        }
        if (ok) {
            vis[i] = true;
            q.push(i);
        }
    }
    while (!q.empty()) {
        int now = q.front();
        q.pop();
        for (int i = 0; i < r; i++) {
            b[i] += m[now][i] - s[now][i];
        }
        for (int i = 0; i < p; i++) {
            if (vis[i]) {
                continue;
            }
            bool ok = true;
            for (int j = 0; j < r; j++) {
                if (s[i][j] > b[j]) {
                    ok = false;
                    break;
                }
            }
            if (ok) {
                vis[i] = true;
                q.push(i);
            }
        }
    }
    for (int i = 0; i < p; i++) {
        if (!vis[i]) {
            return false;
        }
    }
    return true;
}

