def min_gifts(N, M, K, L):
    if L > N - K:
        return -1
    if M > N:
        return -1
    return (L + M - 1) // M

# Example usage:
print(min_gifts(20, 15, 2, 3))
print(min_gifts(10, 11, 2, 4))
