int main(){
    int a,b,c,d,e,na,nb,nc;
    while(cin>>a>>b>>c>>d>>e,a){
        cin>>na>>nb>>nc;
        long long int sum=0;
        if(nc>=d){
            sum=na*a+nb*b+nc*e;
        }else{
            sum+=(nc/d)*d*e;
            nc-=(nc/d)*d;
            if(nc+nb>=d){
                sum+=min(nc*c+(d-nc)*b,e*d);
                nb-=d-nc;
                sum+=nb*b+na*a;
            }else if(na+nb+nc>=d){
                sum+=min(nc*c+nb*b+(d-nc-nb)*a,e*d);
                na-=d-nc-nb;
                sum+=na*a;
            }else{
                sum+=min(na*a+nb*b+nc*c,e*d);
            }
        }
        cout << sum << endl;
    }
    return 0;
}

