int main(){
	int a, b, c, d, e, na, nb, nc;
	while (cin >> a >> b >> c >> d >> e, a || b || c || d || e){
		cin >> na >> nb >> nc;
		if (nc >= d){
			cout << na*a + nb*b + nc*e;
			continue;
		}
		long min = na*a + nb*b + nc*c;
		for (int i = 0; i <= na; i++){
			for (int j = 0; j <= nb; j++){
				if (nc + i + j >= d){
					if (min > (na - i)*a + (nb - j)*b + (nc + i + j)*e){
						min = (na - i)*a + (nb - j)*b + (nc + i + j)*e;
					}
				}
			}
		}
		cout << min << endl;
	}
	return 0;
}

