		while(na!=0||nb!=0||nc!=0){
			if(nc>=d){
				s+=nc*e;
				nc=0;

			}
			else if(nc<d&&nc!=0){
					int i;
					for(i=1;i<=nb;i++){
						if(nc+i>=d){
							if(nc*c+b*i>(nc+i)*e){
							s+=(nc+i)*e;
								nc=0;
								nb-=i;
								f=1;
								break;
							}
						}
							else{
								if(nc*c+b*i>d*e){
									s+=d*e;
									nc=0;
									nb-=i;
									f=1;
									break;
								}
							}
						}
					if(i==nb+1&&f==0){
					int j;
					for(j=1;j<=na;j++){
							if(nc+nb+j>=d){
							if(nc*c+b*nb+a*j>(nc+nb+j)*e){
