								nc=0;
								nb=0;
								na-=j;
								f=1;
								break;
							}
						}
							else{
								if(nc*c+b*nb+a*j>d*e){
									s+=d*e;
									nc=0;
									nb=0;
									na-=j;
									f=1;
									break;
								}
							}
						}
				if(nc*c>d*e&&f==0){
					s+=d*e;
				nc=0;
					break;
				}

				if(j==na+1&&f==0){
					s=na*a+nb*b+nc*c;
					na=0;
					nb=0;
					nc=0;
				}
			}
		}
		else if(nb>=d){
			if(nb*b>nb*e)
			s+=nb*e;
			else
			s+=nb*b;
				nb=0;
		}
		else if(nb<d&&nb!=0){
				int j;
					for(j=1;j<=na;j++){
							if(nb+j>=d){
							if(b*nb+a*j>(nb+j)*e){
							s+=(nb+j)*e;
								nb=0;
								na-=j;
								f=1;
								break;
							}
						}
							else{
								if(b*nb+a*j>d*e){
									s+=d*e;
									nb=0;
									na-=j;
									f=1;
									break;
								}
							}
					}
				if(nb*b>d*e&&f==0){
					s+=d*e;
				nb=0;
					break;
				}
				if(j==na+1&&f==0){
					s+=na*a+nb*b;
					na=0;
					nb=0;
				}
			}
		else if(na>=d){
			if(na*a>na*e)
			s+=na*e;
			else
			s+=na*a;
				na=0;
