void solve(){
  int res = 0;
  for(int i = 0; i < 3; i++){
    res += t[i] * nt[i];
  }

  int sum = res;
  int cnt = 0;

  for(int i = 2; i >= 0; i--){
    while(nt[i] > 0){
      int cal = sum + max(d, cnt) * e;

      res = min(res, cal);
      sum -= t[i];
      nt[i]--;
      cnt++;
    }
  }

  res = min(res, max(d, cnt) * e);

  cout << res << endl;
}

