		for (int i = d; i <= n; i++) {
			int set1 = ruisekiwa(0, i);
			int set2 = ruisekiwa(i, min(n, i + d));
			int set3 = ruisekiwa(min(n, i + d), n);
			if (set1 >= i*e)set1 = i*e;
			if (set2 >= d*e)set2 = d*e;
			minx = min(minx, set1 + set2 + set3);
		}
