using namespace std;
long long a, b, c, d, e, na, nb, nc;
long long F[500000];
long long price(long long L) {
	if (L < nc)return c;
	if (L < nb + nc)return b;
	return a;
}
long long ruisekiwa(long long L, long long R) {
	if (L >= R)0;
	if (L == 0)return F[R - 1];
	return F[R - 1] - F[L - 1];
}
int main() {
	while (true) {
		cin >> a >> b >> c >> d >> e; if (a == 0)break;
		cin >> na >> nb >> nc;
		long long n = na + nb + nc, minx = na*a + nb*b + nc*c;
		for (long long i = 0; i < 500000; i++)F[i] = 0;
		for (long long i = 0; i < n; i++)F[i] = price(i);
		for (long long i = 1; i < n; i++)F[i] += F[i - 1];

		for (long long i = d; i <= n; i++) {
			long long set1 = ruisekiwa(0, i);
			long long set2 = ruisekiwa(i, min(n, i + d));
			long long set3 = ruisekiwa(min(n, i + d), n);
			if (set1 >= i*e)set1 = i*e;
			if (set2 >= d*e)set2 = d*e;
			minx = min(minx, set1 + set2 + set3);
		}
		cout << minx << endl;
	}
	return 0;
}

