		cin >> na >> nb >> nc;
		int res = INF;
		if (nc >= d){
			res=min(res, nc*e + na*a + nb*b);
		}

		for (int i = 0; i <= na + nb + nc;i++){
			int C = max(nc - i, 0);
			int B = max(nb - (i - (nc - C)), 0);
			int A = na - (i - (nc - C) - (nb - B));
			if ((nc - C)*c + (nb - B)*b + (na-A)*a>d*e)
			res = min(res, max(d,i)*e + C*c + B*b + A*a);
		}
		cout << res << endl;
	}

	return 0;
}

