	while (cin >> a >> b >> c >> d >> e, a || b || c || d || e)
	{
		int na, nb, nc;
		cin >> na >> nb >> nc;
		int sum = na * a + nb * b + nc * c;
		sum -= (nc / d) * (c - e) * d;
		nc %= d;
		if (d - nc <= nb)
		{
			if (nc * c + (d - nc) * b > d * e)
			{
				sum = sum - (nc * c + (d - nc) * b) + d * e;
			}
		}
		else
		{
			if (d - nc - nb <= na)
			{
				if (nc * c + nb * b + (d - nc - nb) * a > d * e)
				{
					sum = sum - (nc * c + nb * b + (d - nc - nb) * a) + d * e;
				}
			}
			else
			{
				if (nc * c + nb * b + na * a > d * e)
				{
					sum = sum - (nc * c + nb * b + na * a) + d * e;
				}
			}

		}
		cout << sum << endl;
	}
}

