							dp[i][j][k] = min(dp[i][j][k], dp[i][j-l][k]+m[1]*l);
						}
					}
					if (i > 0) {
						for (long long int l = 1; l <= i; l++) {
							dp[i][j][k] = min(dp[i][j][k], dp[i-l][j][k]+m[0]*l);
						}
					}
					for (int l = 0; l <= k; l++) {
						dp[i][j][k] = min(dp[i][j][k], dp[i][j][l]+dp[i][j][k-l]);
					}
				}
			}
		}
		cout << dp[nn[0]][nn[1]][nn[2]] << endl;
	}
	return 0;
}

